/******************************添加工作人员和志愿者*********************************///添加司机的按钮绑定事件$(document).on('click','button.add_driver_btn',function(){	var staffFrame = window.frames['matchIframe'];  //获取框架	var _table = staffFrame.document.getElementById('choseDriver');	var _checkbox = $(_table).find('tbody input[type=checkbox]:checked');	var driverIds =[];	var driverCheck = $('#choseDriver').find('a');	for(var i=0;i<driverCheck.length;i++){		var driverId = $(driverCheck).eq(i).attr('name');		driverIds.push(driverId);	}	var html = '';	for(var i=0;i<_checkbox.length;i++){		var name = $(_checkbox).eq(i).parent().siblings('.chose_name').text();		var id = $(_checkbox).eq(i).val();		if($.inArray(id,driverIds)<0){			html += '<a type="button" class="btn btn-default btn-sm name_btn_a" name='+id+'>'+name+'</a>';		}	}	$(this).parent().siblings('.name_btn').append(html);});//添加车辆按钮绑定事件$(document).on('click','button.add_car_btn',function(){	var staffFrame = window.frames['matchIframe'];	var _table = staffFrame.document.getElementById('choseCar');	var _checkbox = $(_table).find('tbody input[type=checkbox]:checked');	var carIds = [];	var carCheck = $('#choseCar').find('a');	for(var i=0;i<carCheck.length;i++){		var carId = $(carCheck).eq(i).attr('name');		carIds.push(carIds);	}	var html = '';	for(var i=0;i<_checkbox.length;i++){		var name = $(_checkbox).eq(i).parent().siblings('.chose_name').text();		var id = $(_checkbox).eq(i).val();		if($.inArray(id,carIds)<0){			html += '<a type="button" class="btn btn-default btn-sm name_btn_a" name='+id+'>'+name+'</a>';		}	}	$(this).parent().siblings('.name_btn').append(html);});//$(document).on('click','.name_btn a',function(){	$(this).remove();});//保存按钮点击事件$(document).on('click','button.saveChosen',function(){	var guestCheck = $('.chose_staff_btn').parents('table').children('tbody').find('input[type=checkbox]:checked');	var guestIds = [];	for(var i=0;i<guestCheck.length;i++){		var guestId = $(guestCheck).eq(i).val();		guestIds.push(guestId);	}	var guestIdsStr = guestIds.toString();	$('#chose_staff').find('input[name=guestIds]').val(guestIdsStr);		var driverCheck = $('#choseDriver').find('a');	var driverIds = [];	var carCheck = $('#choseCar').find('a');	var carIds = [];	for(var i=0;i<driverCheck.length;i++){		var driverId = $(driverCheck).eq(i).attr('name');		driverIds.push(driverId);	}	for(var i=0;i<carCheck.length;i++){		var carId = $(carCheck).eq(i).attr('name');		carIds.push(carId);	}	$('#chose_staff').find('input[name=workerIds]').val(driverIds.toString());	$('#chose_staff').find('input[name=supporterIds]').val(carIds.toString());	$(this).parents('form').submit();});