package com.mwi.clmf.controller.admin.adminUser;import com.jfinal.aop.Clear;import com.jfinal.core.Controller;import com.mwi.clmf.model.adminUser.PrivilegeModel;import com.mwi.clmf.model.adminUser.UserModel;import com.mwi.clmf.util.MD5HashUtil;public class AdminLoginController extends Controller{		@Clear	public void index(){		render("mwi_ht_login.jsp");	}		@Clear	public void login(){		String username = getPara("username");		String password = MD5HashUtil.MD5(getPara("password"));		UserModel user = UserModel.uDao.findByUsernameAndPassword(username, password);		if(user != null){			setSessionAttr("privilegeList", PrivilegeModel.pDao.priListByRoleId(user.getLong("role_id")));			setSessionAttr("userId", user.getLong("id"));			redirect("/");		} else {			redirect("/admin_login");		}	}		public void logout(){		removeSessionAttr("userId");		redirect("/admin_login");	}	}