package com.mwi.clmf.controller.admin.staff;import com.jfinal.core.Controller;import com.jfinal.plugin.activerecord.Page;import com.mwi.clmf.model.staff.WorkerInforModel;public class WechatInteractionController extends Controller {		public void index(){		String realname = getPara("realname");		String work_group = getPara("work_group");		System.out.println(work_group);		String unit = getPara("unit");		String post = getPara("post");		String skill = getPara("skill");		String id_no = getPara("id_no");		String phone = getPara("phone");		String wechat_username = getPara("wechat_username");		Integer currentPage = getParaToInt("currentPage") == null ? 1 : getParaToInt("currentPage");		Integer currentSize = getParaToInt("currentSize") == null ? 10 : getParaToInt("currentSize");				String flag = "1";		if("2".equals(getPara("flag"))){			flag = "2";		} else if("3".equals(getPara("flag"))) {			flag = "3";		} 		setAttr("flag", flag);				Page<WorkerInforModel> workerpageList= WorkerInforModel.wiDao.getWorkerList(realname, work_group, unit, post, skill,id_no, phone, wechat_username,currentPage, currentSize,1);		Page<WorkerInforModel> volunteerpageList= WorkerInforModel.wiDao.getWorkerList(realname, work_group, unit, post, skill,id_no, phone, wechat_username,currentPage, currentSize,2);				setAttr("workerList",workerpageList);		setAttr("volunteerList",volunteerpageList);		setAttr("currentPage", currentPage);		setAttr("currentSize", currentSize);		setAttr("realname", realname);		setAttr("work_group", work_group);		setAttr("unit", unit);		setAttr("post", post);		setAttr("skill", skill);		setAttr("id_no", id_no);		setAttr("phone", phone);		setAttr("wechat_username", wechat_username);				render("staffConnect_weichat.jsp");							}}