package com.mwi.clmf.model;import java.util.List;import com.jfinal.plugin.activerecord.Model;@SuppressWarnings("serial")public class ContactPersonModel extends Model<ContactPersonModel> {		public static final ContactPersonModel cpDao = new ContactPersonModel();		public List<ContactPersonModel> getContactPerson(String chinese_name, String english_name, String ch_organ_name, String post, Long user_id){		String sql = "select *,cp.id cp_id from cf_contact_person cp left join cf_guest_infor gi on cp.guest_id=gi.id where cp.user_id="+user_id;		if(chinese_name != null){			sql += " and gi.chinese_name like '%"+chinese_name+"%'";		}		if(english_name != null){			sql += " and gi.english_name like '%"+english_name+"%'";		}		if(ch_organ_name != null){			sql += " and gi.ch_organ_name like '%"+ch_organ_name+"%'";		}		if(post != null){			sql += " and gi.post like '%"+post+"%'";		}		sql += " order by cp.add_time desc";		return cpDao.find(sql) ;	}		public ContactPersonModel getContact(Long userId, Long guestId){		return cpDao.findFirst("select * from cf_contact_person where user_id=? and guest_id=?", userId, guestId);	}}