package com.mwi.clmf.model;import java.util.List;import com.jfinal.plugin.activerecord.Model;@SuppressWarnings("serial")public class ContactRequestModel extends Model<ContactRequestModel> {		public static final ContactRequestModel crDao = new ContactRequestModel();		public List<ContactRequestModel> getRequest(Long guestId){		return crDao.find("select *,cr.id cr_id from cf_contact_request cr left join cf_guest_infor gi on cr.from_user=gi.id where to_user=?",guestId);	}		public ContactRequestModel getRequest(Long from_user, Long to_user, Integer status){		return crDao.findFirst("select * from cf_contact_request where from_user=? and to_user=? and reply_status=?", from_user, to_user, status);	}}