package com.mwi.clmf.model.activity;import java.util.List;import com.jfinal.plugin.activerecord.Model;@SuppressWarnings("serial")public class ActivityCheckRuleStandardModel extends Model<ActivityCheckRuleStandardModel> {	public static final ActivityCheckRuleStandardModel acrsDao = new ActivityCheckRuleStandardModel();		public List<ActivityCheckRuleStandardModel> checkRuleList(){		return acrsDao.find("select acrs.*, (SELECT count(*) from cf_activity_check_management WHERE check_checkRuleStandard_id = acrs.id) count from cf_activity_check_rule_standard acrs");	}}