package com.mwi.clmf.model.adminUser;import java.util.List;import com.jfinal.plugin.activerecord.Model;@SuppressWarnings("serial")public class PrivilegeModel extends Model<PrivilegeModel> {	public static final PrivilegeModel pDao = new PrivilegeModel();		public List<PrivilegeModel> privilegeList(){		return pDao.find("select * from cf_user_privilege");	}			public List<PrivilegeModel> priListByRoleId(Long roleId){		return pDao.find("select up.id, up.parent_id, up.privilege_name, up.action, up.status, up.level, up.imgurl from cf_user_privilege up, cf_user_role_privilege urp where up.id = urp.privilege_id and urp.role_id = "+roleId);	}	}