package com.mwi.clmf.model.adminUser;import java.util.List;import com.jfinal.plugin.activerecord.Model;import com.jfinal.plugin.activerecord.Page;@SuppressWarnings("serial")public class RoleModel extends Model<RoleModel> {	public static final RoleModel rDao = new RoleModel();		public Page<RoleModel> rolePageWithPrivilege(int pageNumber, int pageSize, String roleName){		String sqlPrefix = "SELECT *";		String sqlSurfix = "FROM cf_user_role where 1=1";		if(roleName != null && !"".equals(roleName)){			sqlSurfix += " and role_name like '%"+roleName+"%'";		}				return rDao.paginate(pageNumber, pageSize, sqlPrefix, sqlSurfix);	}		public List<RoleModel> roleList(){		return rDao.find("select * from cf_user_role");	}}