package com.mwi.clmf.model.adminUser;import com.jfinal.plugin.activerecord.Model;import com.jfinal.plugin.activerecord.Page;@SuppressWarnings("serial")public class UserModel extends Model<UserModel> {	public static final UserModel uDao = new UserModel();		public UserModel findByUsernameAndPassword(String username, String password){		return uDao.findFirst("select id, role_id, username, realname, email, status from cf_user where username=? and password=?", username, password);	}		public Page<UserModel> userPage4Pri(int pageNumber, int pageSize, String userName, String realName, String isBind){		String sqlPrefix = "select *, u.id userId";		String sqlSurfix = "from cf_user u left join cf_user_role ur on u.role_id = ur.id where 1=1";				if(userName != null && !"".equals(userName)){			sqlSurfix += " and u.username like '%"+userName+"%'";		}				if(realName != null && !"".equals(realName)){			sqlSurfix += " and u.realname like '%"+realName+"%'";		}				if(isBind != null && !"".equals(isBind)){			if("1".equals(isBind)){				sqlSurfix += " and u.role_id <> 0";			} else {				sqlSurfix += " and u.role_id = 0";			}		}						return uDao.paginate(pageNumber, pageSize, sqlPrefix, sqlSurfix);	}}