package com.mwi.clmf.model.meeting;import java.util.List;import com.jfinal.plugin.activerecord.Model;import com.jfinal.plugin.activerecord.Page;@SuppressWarnings("serial")public class GuestFileModel extends Model<GuestFileModel> {	public final static GuestFileModel fuDao = new GuestFileModel();		public Page<GuestFileModel> guestFilePage(int pageNumber, int pageSize){		String sqlPrefix = "select *";		String sqlSurfix = " from cf_file order by upload_date";				return fuDao.paginate(pageNumber, pageSize, sqlPrefix, sqlSurfix);	}		public List<GuestFileModel> guestFileList(){		return fuDao.find("select * from cf_file order by upload_date");	}	}