package com.mwi.clmf.model.meeting;import java.util.List;import com.jfinal.plugin.activerecord.Model;@SuppressWarnings("serial")public class NewCategoryModel extends Model<NewCategoryModel> {	public final static NewCategoryModel NCDao = new NewCategoryModel();			public List<NewCategoryModel> getCategoryList(){		return NCDao.find("select id,category_name,category_desc,is_show from cf_news_category");	}		public List<NewCategoryModel> catListById(String categoryIds){		return NCDao.find("select * from cf_news_category where id in ("+categoryIds+")");	}}