package com.mwi.clmf.model.meeting;import java.util.List;import com.jfinal.plugin.activerecord.Model;import com.jfinal.plugin.activerecord.Page;@SuppressWarnings("serial")public class VisitorFileModel extends Model<VisitorFileModel> {	public final static VisitorFileModel fuDao = new VisitorFileModel();		public Page<VisitorFileModel> guestFilePage(int pageNumber, int pageSize){		String sqlPrefix = "select *";		String sqlSurfix = " from cf_visitor_file order by upload_date";				return fuDao.paginate(pageNumber, pageSize, sqlPrefix, sqlSurfix);	}		public List<VisitorFileModel> guestFileList(){		return fuDao.find("select * from cf_visitor_file order by upload_date");	}	}