package com.mwi.clmf.util;import java.util.Random;public class MiscUtil {			public static String getCardNo(){		String cardNo = null;		Long timeStamp = System.currentTimeMillis();		String stpStr = timeStamp.toString();		Random r = new Random();		String temp = "ABCDEFGHJKLMNPQRTUVWXYZ";		String prefix = "";		for(int i=0; i<2; i++){			prefix += temp.charAt(r.nextInt(temp.length()));		}		stpStr = stpStr.replace(stpStr.charAt(r.nextInt(stpStr.length())), temp.charAt(r.nextInt(temp.length())));		cardNo = prefix + stpStr;		cardNo = cardNo.replaceAll("0", temp.charAt(r.nextInt(temp.length()))+"").replaceAll("5", temp.charAt(r.nextInt(temp.length()))+"").replaceAll("1", temp.charAt(r.nextInt(temp.length()))+"");		return cardNo;	}	/**	 * 获取随机六位数字组合	 * @return	 */	public static String get6pwd(){		String pwd = "" + ((int)(Math.random()*900000) + 100000);		return pwd;	}	public static void main(String[] args) {		System.out.println(getCardNo() + Math.random() + "  " + get6pwd());	}}